CREATE TABLE if not exists Teacher (Name VARCHAR(45) PRIMARY KEY, Room VARCHAR(5), Building VARCHAR(5), Phone VARCHAR(10), Email VARCHAR(45), Title VARCHAR(45), OfficeHours VARCHAR(65), HowToConnect VARCHAR(65) );
CREATE TABLE if not exists Course (Prefix VARCHAR(5), Number INT, Title VARCHAR(255), Units VARCHAR(3), Prereq VARCHAR(255), Description TEXT, PRIMARY KEY (Prefix, Number) );
CREATE TABLE if not exists Section (CoursePrefix VARCHAR(65), CourseNumberPrefix INT, Number INT, Teacher VARCHAR(45), Type VARCHAR(255), Days VARCHAR(5), StartTime VARCHAR(8), EndTime VARCHAR(8), Room VARCHAR(5), Building VARCHAR(15), Quarter VARCHAR(6), Year INT, PRIMARY KEY (CoursePrefix, CourseNumberPrefix, Number, Quarter, Year, Days), FOREIGN KEY (CoursePrefix, CourseNumberPrefix) REFERENCES Course (Prefix, Number))
